import React, { useState } from 'react';
import { FaFolder, FaFolderOpen, FaFileCode, FaPlus, FaEllipsisV } from 'react-icons/fa';

const FileSidebar = ({ tree, activeFile, setActiveFile, onAddFile, onAddFolder }) => {
  const [expandedFolders, setExpandedFolders] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  const toggleFolder = (folderPath) => {
    setExpandedFolders(prev => ({
      ...prev,
      [folderPath]: !prev[folderPath]
    }));
  };

  const handleContextMenu = (e, filePath) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      filePath
    });
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  // Recursive function to render tree nodes
  const renderTree = (node) => {
    if (node.type === 'file') {
      return (
        <div
          key={node.path}
          className={`file-item ${node.path === activeFile ? 'active' : ''}`}
          onClick={() => setActiveFile(node.path)}
          onContextMenu={(e) => handleContextMenu(e, node.path)}
        >
          <FaFileCode />
          <span>{node.name}</span>
        </div>
      );
    }

    if (node.type === 'folder') {
      return (
        <div key={node.path} className="folder-container">
          <div 
            className="folder-item" 
            onClick={() => toggleFolder(node.path)}
          >
            {expandedFolders[node.path] ? <FaFolderOpen /> : <FaFolder />}
            <span>{node.name}</span>
          </div>
          
          {expandedFolders[node.path] && (
            <div className="folder-contents">
              {node.children.map(child => renderTree(child))}
            </div>
          )}
        </div>
      );
    }

    return null;
  };

  return (
    <div className="file-sidebar">
      <div className="file-sidebar-header">
        <h3>Files</h3>
        <div className="file-actions">
          <button onClick={() => onAddFile()} title="Add File">
            <FaPlus /> File
          </button>
          <button onClick={() => onAddFolder()} title="Add Folder">
            <FaPlus /> Folder
          </button>
        </div>
      </div>

      <div className="file-tree">
        {tree?.children?.map(child => renderTree(child))}
      </div>

      {contextMenu && (
        <div 
          className="context-menu" 
          style={{ top: contextMenu.y, left: contextMenu.x }}
          onClick={closeContextMenu}
        >
          <div className="context-menu-item">Rename</div>
          <div className="context-menu-item">Delete</div>
          <div className="context-menu-item">Duplicate</div>
        </div>
      )}
    </div>
  );
};

export default FileSidebar;
