import React, { useState } from 'react';
import { FaFolder, FaFolderOpen, FaFileCode, FaPlus, FaEllipsisV } from 'react-icons/fa';

const FileSidebar = ({ files, activeFile, setActiveFile, onAddFile, onAddFolder }) => {
  const [expandedFolders, setExpandedFolders] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  const toggleFolder = (folderName) => {
    setExpandedFolders(prev => ({
      ...prev,
      [folderName]: !prev[folderName]
    }));
  };

  const handleContextMenu = (e, file) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      file
    });
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  // Group files by folder
  const fileStructure = files.reduce((acc, file) => {
    const pathParts = file.split('/');
    if (pathParts.length === 1) {
      acc.root = acc.root || [];
      acc.root.push(file);
    } else {
      const folder = pathParts[0];
      acc[folder] = acc[folder] || [];
      acc[folder].push(pathParts[1]);
    }
    return acc;
  }, { root: [] });

  return (
    <div className="file-sidebar">
      <div className="file-sidebar-header">
        <h3>Files</h3>
        <div className="file-actions">
          <button onClick={() => onAddFile()} title="Add File">
            <FaPlus /> File
          </button>
          <button onClick={() => onAddFolder()} title="Add Folder">
            <FaPlus /> Folder
          </button>
        </div>
      </div>

      <div className="file-tree">
        {Object.entries(fileStructure).map(([folder, folderFiles]) => (
          <div key={folder} className="folder-container">
            {folder !== 'root' && (
              <div 
                className="folder-item" 
                onClick={() => toggleFolder(folder)}
              >
                {expandedFolders[folder] ? <FaFolderOpen /> : <FaFolder />}
                <span>{folder}</span>
              </div>
            )}
            
            {(folder === 'root' || expandedFolders[folder]) && (
              <div className="folder-contents">
                {folderFiles.map(file => (
                  <div
                    key={file}
                    className={`file-item ${file === activeFile ? 'active' : ''}`}
                    onClick={() => setActiveFile(folder === 'root' ? file : `${folder}/${file}`)}
                    onContextMenu={(e) => handleContextMenu(e, folder === 'root' ? file : `${folder}/${file}`)}
                  >
                    <FaFileCode />
                    <span>{file}</span>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      {contextMenu && (
        <div 
          className="context-menu" 
          style={{ top: contextMenu.y, left: contextMenu.x }}
          onClick={closeContextMenu}
        >
          <div className="context-menu-item">Rename</div>
          <div className="context-menu-item">Delete</div>
          <div className="context-menu-item">Duplicate</div>
        </div>
      )}
    </div>
  );
};

export default FileSidebar;
