import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useLocation, useParams, useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import '../styles/editor.css';

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);

  const [username, setUsername] = useState('');
  const [files, setFiles] = useState({
    'index.html': '<!-- HTML file -->\n',
    'style.css': '/* CSS file */\n',
    'script.js': '// JS file\n',
  });
  const [activeFile, setActiveFile] = useState('index.html');
  const [isLoading, setIsLoading] = useState(true);
  const [isConnected, setIsConnected] = useState(false);
  const editorRef = useRef(null);
  const iframeRef = useRef(null);

  // Initialize username from various sources
  useEffect(() => {
    const user = (location.state && location.state.username) ||
                 (typeof window !== 'undefined' && window.localStorage && window.localStorage.getItem('username')) ||
                 'Guest';
    setUsername(user);
  }, [location.state]);

  // Fetch files from backend
  const fetchFiles = useCallback(async () => {
    if (!roomId) return;

    try {
      setIsLoading(true);
      const res = await fetch(`http://localhost:5000/api/code/${roomId}`);
      
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      
      const data = await res.json();
      console.log('MongoDB Response:', data);

      if (Array.isArray(data) && data.length > 0) {
        const validFiles = data.filter(
          (file) => file.filename && typeof file.content === 'string'
        );

        if (validFiles.length > 0) {
          const fileMap = {};
          validFiles.forEach((file) => {
            fileMap[file.filename] = file.content;
          });
          console.log("Setting files into editor:", fileMap);
          setFiles(fileMap);
          setActiveFile(Object.keys(fileMap)[0] || 'index.html');
          setIsLoading(false);
          return;
        }
      }

      // If no valid data, initialize defaults and save to DB
      const defaultFiles = {
        'index.html': '<!-- HTML file -->\n',
        'style.css': '/* CSS file */\n',
        'script.js': '// JS file\n',
      };
      
      setFiles(defaultFiles);
      setActiveFile('index.html');

      // Save default files to backend
      for (const [filename, content] of Object.entries(defaultFiles)) {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      }
    } catch (error) {
      console.error('Error loading files:', error);
      // Show user-friendly error message
      alert('Failed to load files. Please try refreshing the page.');
    } finally {
      setIsLoading(false);
    }
  }, [roomId]);

  // Initialize socket connection
  const initializeSocket = useCallback(() => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }

    socketRef.current = io('http://localhost:5000', {
      transports: ['websocket'],
      upgrade: false,
    });

    socketRef.current.on('connect', () => {
      console.log('Socket connected');
      setIsConnected(true);
      if (username && roomId) {
        socketRef.current.emit('join-room', { roomId, username });
      }
    });

    socketRef.current.on('disconnect', () => {
      console.log('Socket disconnected');
      setIsConnected(false);
    });

    socketRef.current.on('receive-code', ({ filePath, code }) => {
      console.log('Received code update:', filePath);
      setFiles((prev) => ({
        ...prev,
        [filePath]: code,
      }));
    });

    socketRef.current.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
      setIsConnected(false);
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [username, roomId]);

  // Main initialization effect
  useEffect(() => {
    if (!roomId) {
      navigate('/room');
      return;
    }

    if (!username) {
      return; // Wait for username to be set
    }

    // First fetch files, then initialize socket
    fetchFiles().then(() => {
      initializeSocket();
    });

    // Store username for future use
    if (typeof window !== 'undefined' && window.localStorage) {
      window.localStorage.setItem('username', username);
    }

    // Cleanup on unmount
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [roomId, username, fetchFiles, initializeSocket, navigate]);

  // Debounced save function
  const saveToBackend = useCallback(
    debounce(async (roomId, filename, content) => {
      try {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      } catch (err) {
        console.error('Error saving file:', err);
      }
    }, 1000),
    []
  );

  const handleCodeChange = (value) => {
    if (!value) value = '';
    
    setFiles((prev) => ({ ...prev, [activeFile]: value }));

    // Emit to socket if connected
    if (socketRef.current && isConnected) {
      socketRef.current.emit('code-change', {
        roomId,
        filePath: activeFile,
        code: value,
      });
    }

    // Save to backend (debounced)
    saveToBackend(roomId, activeFile, value);
  };

  const handleEditorMount = (editor) => {
    editorRef.current = editor;
  };

  const handleRun = () => {
    const html = files['index.html'] || '';
    const css = files['style.css'] || '';
    const js = files['script.js'] || '';

    const combined = `
      <html>
        <head>
          <style>${css}</style>
        </head>
        <body>
          ${html}
          <script>${js}</script>
        </body>
      </html>
    `;

    if (iframeRef.current) {
      const blob = new Blob([combined], { type: 'text/html' });
      const url = URL.createObjectURL(blob);
      iframeRef.current.src = url;
      
      // Clean up previous blob URL
      setTimeout(() => URL.revokeObjectURL(url), 1000);
    }
  };

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  if (isLoading) {
    return (
      <div className="editor-wrapper">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh' 
        }}>
          <div>Loading files...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="editor-wrapper">
      <div className="sidebar">
        <FileSidebar
          files={Object.keys(files)}
          activeFile={activeFile}
          setActiveFile={setActiveFile}
        />
      </div>

      <div className="main-editor">
        <div className="editor-header">
          <div>
            <strong>Room:</strong> {roomId}
            <br />
            <span>Welcome, {username}!</span>
            <br />
            <span style={{ 
              color: isConnected ? 'green' : 'red', 
              fontSize: '12px' 
            }}>
              {isConnected ? '● Connected' : '● Disconnected'}
            </span>
          </div>
          <button className="run-button" onClick={handleRun}>
            Run ▶
          </button>
        </div>

        <Editor
          height="50vh"
          language={getLanguage(activeFile)}
          value={files[activeFile] || ""}
          onChange={handleCodeChange}
          onMount={handleEditorMount}
          theme="vs-dark"
          options={{
            minimap: { enabled: false },
            fontSize: 14,
            wordWrap: 'on',
            automaticLayout: true,
          }}
        />

        <div className="output-panel">
          <iframe
            ref={iframeRef}
            title="Output Preview"
            className="output-frame"
            sandbox="allow-scripts allow-same-origin"
          />
        </div>
      </div>
    </div>
  );
}

// Simple debounce function
function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

export default EditorPage;
