import React, { useEffect, useRef, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import '../styles/editor.css';

const socket = io('http://localhost:5000');

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();

  const username =
    (location.state && location.state.username) ||
    localStorage.getItem('username') ||
    'Guest';

  const [files, setFiles] = useState({
    'index.html': '<!-- HTML file -->\n',
    'style.css': '/* CSS file */\n',
    'script.js': '// JS file\n',
  });

  const [activeFile, setActiveFile] = useState('index.html');
  const editorRef = useRef(null);
  const iframeRef = useRef(null);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/code/${roomId}`);
        const data = await res.json();
        console.log('MongoDB Response:', data);

        if (Array.isArray(data) && data.length > 0) {
          const validFiles = data.filter(
            (file) => file.fileName && typeof file.content === 'string'
          );

          if (validFiles.length > 0) {
            const fileMap = {};
            validFiles.forEach((file) => {
              fileMap[file.fileName] = file.content;
            });
            setFiles(fileMap);
            return;
          }
        }

        // If no valid data, initialize defaults and save to DB
        const defaultFiles = {
          'index.html': '<!-- HTML file -->\n',
          'style.css': '/* CSS file */\n',
          'script.js': '// JS file\n',
        };
        setFiles(defaultFiles);

        for (const [filename, content] of Object.entries(defaultFiles)) {
          await fetch('http://localhost:5000/api/code/save', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ roomId, filename, content }),
          });
        }
      } catch (error) {
        console.error('Error loading files:', error);
      }
    };

    fetchFiles();

    if (username) {
      localStorage.setItem('username', username);
      socket.emit('join-room', { roomId, username });
    }

    socket.on('receive-code', ({ filePath, code }) => {
      setFiles((prev) => ({
        ...prev,
        [filePath]: code,
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, [roomId, username]);

  const handleCodeChange = (value) => {
    setFiles((prev) => ({ ...prev, [activeFile]: value }));

    socket.emit('code-change', {
      roomId,
      filePath: activeFile,
      code: value,
    });

    fetch('http://localhost:5000/api/code/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        roomId,
        filename: activeFile,
        content: value,
      }),
    }).catch((err) => console.error('Error saving file:', err));
  };

  const handleEditorMount = (editor) => {
    editorRef.current = editor;
  };

  const handleRun = () => {
    const html = files['index.html'] || '';
    const css = files['style.css'] || '';
    const js = files['script.js'] || '';

    const combined = `
      <html>
        <head>
          <style>${css}</style>
        </head>
        <body>
          ${html}
          <script>${js}<\/script>
        </body>
      </html>
    `;

    const blob = new Blob([combined], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    iframeRef.current.src = url;
  };

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  return (
    <div className="editor-wrapper">
      <div className="sidebar">
        <FileSidebar
          files={Object.keys(files)}
          activeFile={activeFile}
          setActiveFile={setActiveFile}
        />
      </div>

      <div className="main-editor">
        <div className="editor-header">
          <div>
            <strong>Room:</strong> {roomId}
            <br />
            <span>Welcome, {username}!</span>
          </div>
          <button className="run-button" onClick={handleRun}>
            Run â–¶
          </button>
        </div>

        <Editor
          height="50vh"
          language={getLanguage(activeFile)}
          value={files[activeFile]}
          onChange={handleCodeChange}
          onMount={handleEditorMount}
          theme="vs-dark"
        />

        <div className="output-panel">
          <iframe
            ref={iframeRef}
            title="Output Preview"
            className="output-frame"
          />
        </div>
      </div>
    </div>
  );
}

export default EditorPage;
