import React, { useState } from 'react';
import '../styles/FileSidebar.css';

const FileSidebar = ({
  files,
  activeFile,
  setActiveFile,
  onAddFile,
  onDownload,
  onDeleteFile,
  onRenameFile
}) => {
  const [expandedFolders, setExpandedFolders] = useState({});

  const toggleFolder = (folder) => {
    setExpandedFolders(prev => ({ ...prev, [folder]: !prev[folder] }));
  };

  // Helper to create a tree structure
  const buildTree = (paths) => {
    const tree = {};
    paths.forEach(path => {
      const parts = path.split('/');
      let current = tree;
      parts.forEach((part, i) => {
        if (!current[part]) {
          current[part] = i === parts.length - 1 ? null : {};
        }
        current = current[part];
      });
    });
    return tree;
  };

  const renderTree = (node, prefix = '') => {
    return Object.entries(node).map(([name, children]) => {
      const fullPath = prefix ? `${prefix}/${name}` : name;
      if (children === null) {
        // It's a file
        return (
          <div key={fullPath} className="file-item-wrapper">
            <div
              className={`file-item ${fullPath === activeFile ? 'active' : ''}`}
              onClick={() => setActiveFile(fullPath)}
            >
              üìÑ {name}
            </div>
            <button className="rename-button" onClick={() => handleRename(fullPath)}>‚úèÔ∏è</button>
            <button className="delete-button" onClick={() => onDeleteFile(fullPath)}>üóëÔ∏è</button>
          </div>
        );
      } else {
        // It's a folder
        return (
          <div key={fullPath} className="folder-group">
            <div className="folder-name" onClick={() => toggleFolder(fullPath)}>
              üìÅ {name}
            </div>
            {expandedFolders[fullPath] && (
              <div className="folder-children">
                {renderTree(children, fullPath)}
              </div>
            )}
          </div>
        );
      }
    });
  };

  const handleRename = (path) => {
    const newName = prompt('Enter new name (with extension):', path.split('/').pop());
    if (newName) {
      const base = path.includes('/') ? path.substring(0, path.lastIndexOf('/')) : '';
      const newPath = base ? `${base}/${newName}` : newName;
      onRenameFile(path, newPath);
    }
  };

  const tree = buildTree(Object.keys(files));

  return (
    <div className="file-sidebar">
      <div className="sidebar-controls">
        <button className="add-file-button" onClick={onAddFile}>‚ûï Add File</button>
        <button className="download-button" onClick={onDownload}>‚¨á Download</button>
      </div>

      <div className="file-list">
        {renderTree(tree)}
      </div>
    </div>
  );
};

export default FileSidebar;
