import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useLocation, useParams, useNavigate, useBeforeUnload } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import '../styles/editor.css';

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const initializedRef = useRef(false);
  const currentUserRef = useRef(null);
  const editorRef = useRef(null);
  const iframeRef = useRef(null);

  const [username, setUsername] = useState('');
  const [files, setFiles] = useState(null);
  const [activeFile, setActiveFile] = useState('index.html');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [users, setUsers] = useState([]);
  const [activeUsers, setActiveUsers] = useState({});
  const [typingIndicator, setTypingIndicator] = useState(null);

  // Debounced save function
  const debouncedSave = useCallback(
    debounce(async (roomId, filename, content) => {
      try {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      } catch (err) {
        console.error('Error saving file:', err);
      }
    }, 1000),
    []
  );

  // Fetch files from backend
  const fetchFiles = useCallback(async () => {
    if (!roomId) return;

    try {
      const response = await fetch(`http://localhost:5000/api/code/${roomId}`);
      
      if (!response.ok) {
        if (response.status === 404) {
          const defaultFiles = {
            'index.html': '<!-- HTML file -->\n',
            'style.css': '/* CSS file */\n',
            'script.js': '// JS file\n',
          };
          
          await Promise.all(
            Object.entries(defaultFiles).map(([filename, content]) =>
              debouncedSave(roomId, filename, content)
          )
          )
          return defaultFiles;
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      const requiredFiles = ['index.html', 'style.css', 'script.js'];
      const completeFiles = { ...data };
      
      requiredFiles.forEach(file => {
        if (!completeFiles[file]) {
          completeFiles[file] = file.endsWith('.html') ? '<!-- HTML file -->\n' :
                          file.endsWith('.css') ? '/* CSS file */\n' :
                          '// JS file\n';
        }
      });
      
      return completeFiles;
    } catch (error) {
      console.error('Error loading files:', error);
      return {
        'index.html': '<!-- HTML file -->\n',
        'style.css': '/* CSS file */\n',
        'script.js': '// JS file\n',
      };
    }
  }, [roomId, debouncedSave]);

  // Initialize socket connection
  const initializeSocket = useCallback((filesData) => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }

    const socket = io('http://localhost:5000', {
      transports: ['websocket'],
      upgrade: false,
      reconnectionAttempts: 3,
      reconnectionDelay: 3000,
    });

    socket.on('connect', () => {
      setConnectionStatus('Connected');
      currentUserRef.current = socket.id;
      socket.emit('join-room', { 
        roomId, 
        username,
        files: filesData 
      });
    });

    socket.on('disconnect', () => {
      setConnectionStatus('Disconnected');
    });

    socket.on('room-state', ({ files: roomFiles, users: roomUsers }) => {
      setFiles(roomFiles);
      setUsers(roomUsers);
    });

    socket.on('code-update', ({ filePath, code, sender, position }) => {
      if (sender !== currentUserRef.current) {
        setFiles(prev => {
          const newFiles = { ...prev, [filePath]: code };
          
          // Update editor content if it's the active file
          if (filePath === activeFile && editorRef.current) {
            const model = editorRef.current.getModel();
            if (model && model.getValue() !== code) {
              // Preserve cursor position
              const selection = editorRef.current.getSelection();
              model.setValue(code);
              if (selection) {
                editorRef.current.setSelection(selection);
              }
            }
          }
          
          return newFiles;
        });

        setActiveUsers(prev => ({
          ...prev,
          [sender]: { 
            ...prev[sender], 
            position, 
            activeFile: filePath 
          }
        }));
      }
    });

    socket.on('user-joined', ({ userId, username }) => {
      setUsers(prev => [...prev, { id: userId, username }]);
    });

    socket.on('user-left', ({ userId }) => {
      setUsers(prev => prev.filter(u => u.id !== userId));
      setActiveUsers(prev => {
        const newActive = { ...prev };
        delete newActive[userId];
        return newActive;
      });
    });

    socket.on('user-activity', ({ userId, username, position, activeFile }) => {
      if (userId !== currentUserRef.current) {
        setActiveUsers(prev => ({
          ...prev,
          [userId]: { username, position, activeFile }
        }));
        
        if (activeFile === activeFile) {
          setTypingIndicator(`${username} is typing...`);
          setTimeout(() => setTypingIndicator(null), 2000);
        }
      }
    });

    socketRef.current = socket;
    return socket;
  }, [username, roomId, activeFile]);

  // Main initialization effect
  useEffect(() => {
    if (!roomId) {
      navigate('/room');
      return;
    }

    if (initializedRef.current) return;
    initializedRef.current = true;

    const user = (location.state && location.state.username) ||
                 localStorage.getItem('username') ||
                 'Guest';
    setUsername(user);

    const initialize = async () => {
      try {
        const filesData = await fetchFiles();
        setFiles(filesData);
        initializeSocket(filesData);
        localStorage.setItem('username', user);
      } catch (error) {
        console.error('Initialization error:', error);
        setConnectionStatus('Initialization failed');
      }
    };

    initialize();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [roomId, navigate, location.state, fetchFiles, initializeSocket]);

  // Track cursor position
  useEffect(() => {
    const handleCursorChange = () => {
      if (socketRef.current?.connected && editorRef.current) {
        const selection = editorRef.current.getSelection();
        const position = {
          lineNumber: selection.positionLineNumber,
          column: selection.positionColumn
        };
        
        socketRef.current.emit('cursor-position', {
          roomId,
          filePath: activeFile,
          position
        });
      }
    };

    const interval = setInterval(handleCursorChange, 500); // More frequent updates
    return () => clearInterval(interval);
  }, [roomId, activeFile]);

  // Handle code changes
  const handleCodeChange = useCallback((value) => {
    if (!value) value = '';
    
    setFiles(prev => {
      if (prev[activeFile] !== value) {
        const newFiles = { ...prev, [activeFile]: value };
        
        if (socketRef.current?.connected && editorRef.current) {
          const selection = editorRef.current.getSelection();
          const position = selection ? {
            lineNumber: selection.positionLineNumber,
            column: selection.positionColumn
          } : null;
          
          socketRef.current.emit('code-change', {
            roomId,
            filePath: activeFile,
            code: value,
            position,
            sender: currentUserRef.current
          });
        }
        
        debouncedSave(roomId, activeFile, value);
        return newFiles;
      }
      return prev;
    });
  }, [activeFile, roomId, debouncedSave]);
useEffect(() => {
  if (editorRef.current && files && activeFile) {
    const model = editorRef.current.getModel();
    const currentContent = files[activeFile] || '';
    
    if (model && model.getValue() !== currentContent) {
      const selection = editorRef.current.getSelection();
      model.setValue(currentContent);
      if (selection) {
        editorRef.current.setSelection(selection);
      }
    }
  }
}, [files, activeFile]);
  // Update editor content when files or active file changes
  useEffect(() => {
    if (editorRef.current && files && activeFile) {
      const model = editorRef.current.getModel();
      const updatedCode = files[activeFile];

      if (model && updatedCode !== model.getValue()) {
        const selection = editorRef.current.getSelection();
        model.setValue(updatedCode);
        if (selection) {
          editorRef.current.setSelection(selection);
        }
      }
    }
  }, [files, activeFile]);

  // Handle running the code
  const handleRun = () => {
    if (!files || !iframeRef.current) return;

    const html = files['index.html'] || '';
    const css = files['style.css'] || '';
    const js = files['script.js'] || '';

    const combined = `
      <!DOCTYPE html>
      <html>
        <head>
          <style>${css}</style>
        </head>
        <body>
          ${html}
          <script>${js}</script>
        </body>
      </html>
    `;

    const blob = new Blob([combined], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    iframeRef.current.src = url;
    setTimeout(() => URL.revokeObjectURL(url), 1000);
  };

  // Cleanup on page unload
  useBeforeUnload(() => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }
  });

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  if (!files) {
    return (
      <div className="editor-wrapper">
        <div className="loading-screen">
          <div>Loading editor...</div>
          <div>{connectionStatus}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="editor-wrapper">
      <div className="collaboration-bar">
        <div className="online-users">
          {users.map(user => (
            <div key={user.id} className="user-badge">
              {user.username}
              {activeUsers[user.id]?.activeFile === activeFile && (
                <span 
                  className="user-cursor"
                  data-username={user.username}
                  style={{ 
                    top: `${(activeUsers[user.id]?.position?.lineNumber || 0) * 18}px`,
                    left: `${(activeUsers[user.id]?.position?.column || 0) * 8}px`
                  }}
                />
              )}
            </div>
          ))}
        </div>
        {typingIndicator && <div className="typing-indicator">{typingIndicator}</div>}
      </div>

      <div className="sidebar">
        <FileSidebar
          files={Object.keys(files)}
          activeFile={activeFile}
          setActiveFile={setActiveFile}
        />
      </div>

      <div className="main-editor">
        <div className="editor-header">
          <div>
            <strong>Room:</strong> {roomId}
            <br />
            <span>Welcome, {username}!</span>
            <br />
            <span className={`connection-status ${connectionStatus === 'Connected' ? 'connected' : 'disconnected'}`}>
              ● {connectionStatus}
            </span>
          </div>
          <button className="run-button" onClick={handleRun}>
            Run ▶
          </button>
        </div>

        <Editor
          height="50vh"
          language={getLanguage(activeFile)}
          value={files[activeFile] || ""}
          onChange={handleCodeChange}
          onMount={editor => editorRef.current = editor}
          theme="vs-dark"
          options={{
            minimap: { enabled: false },
            fontSize: 14,
            wordWrap: 'on',
            automaticLayout: true,
          }}
        />

        <div className="output-panel">
          <iframe
            ref={iframeRef}
            title="Output Preview"
            className="output-frame"
            sandbox="allow-scripts allow-same-origin"
          />
        </div>
      </div>
    </div>
  );
}

function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

export default EditorPage;
