import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useLocation, useParams, useNavigate, useBeforeUnload } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';
import '../styles/editor.css';

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const editorRef = useRef(null);
  const iframeRef = useRef(null);
  const initializedRef = useRef(false);

  const [username, setUsername] = useState('');
  const [files, setFiles] = useState({});
  const [activeFile, setActiveFile] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  const debouncedSave = useCallback(
    debounce(async (roomId, filename, content) => {
      try {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      } catch (err) {
        console.error('Error saving file:', err);
      }
    }, 1000),
    []
  );

  const fetchFiles = useCallback(async () => {
    if (!roomId) return;
    try {
      const res = await fetch(`http://localhost:5000/api/code/${roomId}`);
      if (!res.ok) throw new Error('No files found');
      const data = await res.json();
      return data;
    } catch (e) {
      const defaultFiles = {
        'index.html': '<!-- HTML file -->\n',
        'style.css': '/* CSS file */\n',
        'script.js': '// JS file\n',
      };
      await Promise.all(
        Object.entries(defaultFiles).map(([f, c]) => debouncedSave(roomId, f, c))
      );
      return defaultFiles;
    }
  }, [roomId, debouncedSave]);

  const initializeSocket = useCallback((filesData) => {
    const socket = io('http://localhost:5000');

    socket.on('connect', () => {
      setConnectionStatus('Connected');
      socket.emit('join-room', { roomId, username, files: filesData });
    });

    socket.on('receive-code', ({ filePath, code }) => {
      setFiles(prev => ({
        ...prev,
        [filePath]: code
      }));
    });

    socketRef.current = socket;
  }, [roomId, username]);

  useEffect(() => {
    if (!roomId) navigate('/room');
    if (initializedRef.current) return;
    initializedRef.current = true;

    const user = location.state?.username || localStorage.getItem('username') || 'Guest';
    setUsername(user);

    (async () => {
      const loadedFiles = await fetchFiles();
      setFiles(loadedFiles);
      setActiveFile(Object.keys(loadedFiles)[0]);
      initializeSocket(loadedFiles);
    })();
  }, [roomId, navigate, location.state, fetchFiles, initializeSocket]);

  const handleCodeChange = (value) => {
    if (!value) value = '';
    setFiles(prev => ({ ...prev, [activeFile]: value }));
    socketRef.current.emit('code-change', { roomId, filePath: activeFile, code: value });
    debouncedSave(roomId, activeFile, value);
  };

  const handleRun = () => {
    if (!files || !iframeRef.current) return;

    const html = Object.entries(files).filter(([f]) => f.endsWith('.html')).map(([_, c]) => c).join('\n');
    const css = Object.entries(files).filter(([f]) => f.endsWith('.css')).map(([_, c]) => `<style>${c}</style>`).join('\n');
    const js = Object.entries(files).filter(([f]) => f.endsWith('.js')).map(([_, c]) => `<script>${c}</script>`).join('\n');

    const combined = `
      <!DOCTYPE html>
      <html>
        <head>${css}</head>
        <body>${html}${js}</body>
      </html>
    `;

    const blob = new Blob([combined], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    iframeRef.current.src = url;
    setTimeout(() => URL.revokeObjectURL(url), 1000);
  };

  const handleDownloadProject = () => {
    const zip = new JSZip();
    Object.entries(files).forEach(([name, content]) => zip.file(name, content));
    zip.generateAsync({ type: 'blob' }).then(blob => {
      saveAs(blob, `${roomId || 'project'}.zip`);
    });
  };

  const handleAddFile = () => {
    const fileName = prompt('Enter new file name (e.g. folder/utils.js):');
    if (!fileName || files[fileName]) return;

    setFiles(prev => ({ ...prev, [fileName]: '' }));
    setActiveFile(fileName);
    socketRef.current.emit('code-change', { roomId, filePath: fileName, code: '' });
    debouncedSave(roomId, fileName, '');
  };

  const handleDeleteFile = (fileName) => {
    if (!fileName) return;
    const updated = { ...files };
    delete updated[fileName];
    setFiles(updated);
    if (activeFile === fileName) {
      const remaining = Object.keys(updated);
      setActiveFile(remaining[0] || '');
    }
  };
const handleRenameFile = (oldName, newName) => {
  setFiles(prevFiles => {
    const updated = { ...prevFiles };
    updated[newName] = updated[oldName];
    delete updated[oldName];
    return updated;
  });

  if (activeFile === oldName) {
    setActiveFile(newName);
  }
};

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  if (!files || Object.keys(files).length === 0) {
    return <div className="loading-screen">Loading editor...</div>;
  }

  return (
    <div className="editor-wrapper">
      <div className="sidebar">
        <FileSidebar
          files={Object.keys(files)}
          activeFile={activeFile}
          setActiveFile={setActiveFile}
          onAddFile={handleAddFile}
          onRenameFile={handleRenameFile}
          onDownload={handleDownloadProject}
          onDeleteFile={handleDeleteFile}
        />
      </div>

      <div className="main-editor">
        <div className="editor-header">
          <strong>{activeFile}</strong>
          <button onClick={handleRun}>â–¶ Run</button>
        </div>

        <Editor
          height="50vh"
          language={getLanguage(activeFile)}
          value={files[activeFile]}
          onChange={handleCodeChange}
          onMount={editor => editorRef.current = editor}
          theme="vs-dark"
          options={{ minimap: { enabled: false }, fontSize: 14, wordWrap: 'on', automaticLayout: true }}
        />

        <div className="output-panel">
          <iframe ref={iframeRef} title="Output" className="output-frame" sandbox="allow-scripts allow-same-origin" />
        </div>
      </div>
    </div>
  );
}

function debounce(func, wait) {
  let timeout;
  return function (...args) {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
}

export default EditorPage;
