import React, { useState } from 'react';
import { FaFolder, FaFolderOpen, FaFileCode, FaPlus, FaEllipsisV } from 'react-icons/fa';

const FileSidebar = ({ tree, activeFile, setActiveFile, onAddFile, onAddFolder, onAddFileToFolder, roomId }) => {
  const [expandedFolders, setExpandedFolders] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  const toggleFolder = (folderPath) => {
    setExpandedFolders(prev => ({
      ...prev,
      [folderPath]: !prev[folderPath]
    }));
  };

  const handleContextMenu = (e, filePath, type) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      filePath,
      type
    });
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  const handleCreateFileInFolder = (folderPath) => {
    const newFileName = prompt('Enter new file name (e.g., about.html)');
    if (!newFileName) return;
    const fullPath = folderPath + newFileName;
    onAddFileToFolder && onAddFileToFolder(fullPath);
    closeContextMenu();
  };

  const handleRename = async (oldPath) => {
    const newName = prompt('Enter new name');
    if (!newName || newName === oldPath) return;

    const isFolder = oldPath.endsWith('/');
    const newPath = isFolder
      ? oldPath.split('/').slice(0, -2).join('/') + '/' + newName + '/'
      : oldPath.split('/').slice(0, -1).join('/') + '/' + newName;

    const res = await fetch('http://localhost:5000/api/code/rename', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ roomId, oldPath, newPath })
    });

    if (res.ok) {
      alert('Renamed successfully. Please refresh to see the change.');
    } else {
      alert('Rename failed. Check console for errors.');
      console.error('Rename failed');
    }
  };

  const handleDelete = async (path) => {
    const res = await fetch(`http://localhost:5000/api/code/delete/${roomId}`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ path })
    });

    if (res.ok) {
      alert('Deleted successfully. Please refresh to see the change.');
    } else {
      alert('Delete failed. Check console for errors.');
      console.error('Delete failed');
    }
  };

  const renderTree = (node) => {
    if (node.type === 'file') {
      return (
        <div
          key={node.path}
          className={`file-item ${node.path === activeFile ? 'active' : ''}`}
          onClick={() => setActiveFile(node.path)}
        >
          <FaFileCode />
          <span>{node.name}</span>
          <FaEllipsisV onClick={(e) => handleContextMenu(e, node.path, 'file')} />
        </div>
      );
    }

    if (node.type === 'folder') {
      return (
        <div key={node.path} className="folder-container">
          <div className="folder-item" onClick={() => toggleFolder(node.path)}>
            {expandedFolders[node.path] ? <FaFolderOpen /> : <FaFolder />}
            <span>{node.name}</span>
            <FaEllipsisV onClick={(e) => handleContextMenu(e, node.path, 'folder')} />
          </div>

          {expandedFolders[node.path] && (
            <div className="folder-contents">
              {node.children.map(child => renderTree(child))}
            </div>
          )}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="file-sidebar">
      <div className="file-sidebar-header">
        <h3>Files</h3>
        <div className="file-actions">
          <button onClick={onAddFile} title="Add File">
            <FaPlus /> File
          </button>
          <button onClick={onAddFolder} title="Add Folder">
            <FaPlus /> Folder
          </button>
        </div>
      </div>

      <div className="file-tree">
        {tree?.children?.map(child => renderTree(child))}
      </div>

      {contextMenu && (
        <div
          className="context-menu"
          style={{ top: contextMenu.y, left: contextMenu.x }}
        >
          {contextMenu.type === 'folder' && (
            <>
              <div className="context-menu-item" onClick={() => handleCreateFileInFolder(contextMenu.filePath)}>New File</div>
              <div className="context-menu-item">New Folder</div>
            </>
          )}
          <div className="context-menu-item" onClick={() => handleRename(contextMenu.filePath)}>Rename</div>
          <div className="context-menu-item" onClick={() => handleDelete(contextMenu.filePath)}>Delete</div>
        </div>
      )}
    </div>
  );
};

export default FileSidebar;
