import React from 'react';
import '../styles/FileSidebar.css';

const FileSidebar = ({ files, activeFile, setActiveFile, onAddFile, onDownload, onDeleteFile }) => {
  return (
    <div className="file-sidebar">
      <div className="sidebar-controls">
        <button className="add-file-button" onClick={onAddFile}>‚ûï Add File</button>
        <button className="download-button" onClick={onDownload}>‚¨á Download</button>
      </div>

      <div className="file-list">
        {files.map((file) => (
          <div key={file} className="file-item-wrapper">
            <div
              className={`file-item ${file === activeFile ? 'active' : ''}`}
              onClick={() => setActiveFile(file)}
            >
              üìÑ {file}
            </div>
            <button
              className="delete-button"
              onClick={() => onDeleteFile(file)}
              title="Delete file"
            >
              üóëÔ∏è
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileSidebar;
