import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useLocation, useParams, useNavigate, useBeforeUnload } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import '../styles/editor.css';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';
import debounce from 'lodash.debounce';

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const initializedRef = useRef(false);
  const currentUserRef = useRef(null);
  const editorRef = useRef(null);
  const iframeRef = useRef(null);
  const isUpdatingFromSocket = useRef(false);
  const lastChangeTimestamp = useRef(0);

  const [username, setUsername] = useState('');
  const [files, setFiles] = useState({});
  const [activeFile, setActiveFile] = useState('index.html');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [users, setUsers] = useState([]);
  const [activeUsers, setActiveUsers] = useState({});
  const [typingUsers, setTypingUsers] = useState({});
  const [userCursors, setUserCursors] = useState({});

  // Colors for different users
  const userColors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#98D8C8'];
  const getUserColor = (userId) => {
    const index = Object.keys(activeUsers).indexOf(userId);
    return userColors[index % userColors.length];
  };

  // Debounced save function using lodash.debounce
  const debouncedSave = useCallback(
    debounce(async (roomId, filename, content) => {
      try {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      } catch (err) {
        console.error('Error saving file:', err);
      }
    }, 2000),
    []
  );

  // Cleanup debounce on unmount
  useEffect(() => {
    return () => {
      debouncedSave.cancel();
    };
  }, [debouncedSave]);

  // Fetch files from backend
  // In EditorPage.js, update the fetchFiles function:
const fetchFiles = useCallback(async () => {
  if (!roomId) return;

  try {
    const response = await fetch(`http://localhost:5000/api/code/${roomId}`);
    
    if (!response.ok) {
      if (response.status === 404) {
        const defaultFiles = {
          'index.html': '<!DOCTYPE html>\n<html>\n<head>\n    <title>My App</title>\n</head>\n<body>\n    <h1>Hello World!</h1>\n</body>\n</html>',
          'style.css': 'body { font-family: Arial, sans-serif; margin: 0; padding: 20px; }',
          'script.js': 'console.log("Hello from JavaScript!");'
        };
        
        await Promise.all(
          Object.entries(defaultFiles).map(([filename, content]) =>
            debouncedSave(roomId, filename, content)
        )
      )
        return defaultFiles;
      }
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error loading files:', error);
    return {
      'index.html': '<!DOCTYPE html>\n<html>\n<head>\n    <title>My App</title>\n</head>\n<body>\n    <h1>Hello World!</h1>\n</body>\n</html>',
      'style.css': 'body { font-family: Arial, sans-serif; margin: 0; padding: 20px; }',
      'script.js': 'console.log("Hello from JavaScript!");'
    };
  }
}, [roomId, debouncedSave]);
  // Initialize socket connection
  const initializeSocket = useCallback((filesData) => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }

    const socket = io('http://localhost:5000', {
      transports: ['websocket'],
      upgrade: false,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    });

    socket.on('connect', () => {
      console.log('🟢 Connected to server');
      setConnectionStatus('Connected');
      currentUserRef.current = socket.id;
      socket.emit('join-room', { 
        roomId, 
        username,
        files: filesData 
      });
    });

    socket.on('disconnect', () => {
      console.log('🔴 Disconnected from server');
      setConnectionStatus('Disconnected');
    });

    socket.on('reconnect', () => {
      console.log('🟡 Reconnected to server');
      setConnectionStatus('Connected');
      socket.emit('join-room', { 
        roomId, 
        username,
        files: files 
      });
    });

    socket.on('room-state', ({ files: roomFiles, users: roomUsers }) => {
      console.log('📋 Received room state:', { files: Object.keys(roomFiles), users: roomUsers.length });
      setFiles(roomFiles);
      setUsers(roomUsers);
    });

    socket.on('user-file-switch', ({ userId, username: switchUsername, filePath }) => {
      if (userId !== currentUserRef.current) {
        setUserCursors(prev => ({
          ...prev,
          [userId]: { 
            ...prev[userId], 
            activeFile: filePath,
            username: switchUsername
          }
        }));
      }
    });

    socket.on('code-update', ({ filePath, code, sender, position, username: senderUsername }) => {
      if (sender !== currentUserRef.current) {
        console.log('📝 Code update from', senderUsername, 'for', filePath);
        
        isUpdatingFromSocket.current = true;
        
        setFiles(prev => {
          const newFiles = { ...prev, [filePath]: code };
          
          if (filePath === activeFile && editorRef.current) {
            const model = editorRef.current.getModel();
            if (model && model.getValue() !== code) {
              const currentSelection = editorRef.current.getSelection();
              const scrollInfo = editorRef.current.getScrollTop();
              
              model.setValue(code);
              
              if (currentSelection && !position) {
                editorRef.current.setSelection(currentSelection);
                editorRef.current.setScrollTop(scrollInfo);
              }
            }
          }
          
          return newFiles;
        });

        if (position) {
          setUserCursors(prev => ({
            ...prev,
            [sender]: { 
              ...prev[sender], 
              position, 
              activeFile: filePath,
              username: senderUsername
            }
          }));
        }

        if (filePath === activeFile) {
          setTypingUsers(prev => ({
            ...prev,
            [sender]: { username: senderUsername, timestamp: Date.now() }
          }));
        }

        setTimeout(() => {
          isUpdatingFromSocket.current = false;
        }, 100);
      }
    });

    socket.on('user-joined', ({ userId, username: newUsername }) => {
      console.log('👤 User joined:', newUsername);
      setUsers(prev => {
        const exists = prev.find(u => u.id === userId);
        if (!exists) {
          return [...prev, { id: userId, username: newUsername }];
        }
        return prev;
      });
    });

    socket.on('new-file', ({ filename }) => {
      setFiles(prev => {
        if (!prev[filename]) {
          return { ...prev, [filename]: '' };
        }
        return prev;
      });
    });

    socket.on('file-removed', ({ filename }) => {
      setFiles(prev => {
        const updated = { ...prev };
        delete updated[filename];
        return updated;
      });
    });

    socket.on('user-left', ({ userId, username: leftUsername }) => {
      console.log('👤 User left:', leftUsername);
      setUsers(prev => prev.filter(u => u.id !== userId));
      setActiveUsers(prev => {
        const newActive = { ...prev };
        delete newActive[userId];
        return newActive;
      });
      setUserCursors(prev => {
        const newCursors = { ...prev };
        delete newCursors[userId];
        return newCursors;
      });
      setTypingUsers(prev => {
        const newTyping = { ...prev };
        delete newTyping[userId];
        return newTyping;
      });
    });

    socket.on('cursor-position', ({ userId, username: cursorUsername, position, activeFile: userActiveFile }) => {
      if (userId !== currentUserRef.current && userActiveFile === activeFile) {
        setUserCursors(prev => ({
          ...prev,
          [userId]: { 
            position, 
            activeFile: userActiveFile, 
            username: cursorUsername,
            timestamp: Date.now()
          }
        }));
      }
    });

    socket.on('user-typing', ({ userId, username: typingUsername, filePath }) => {
      if (userId !== currentUserRef.current && filePath === activeFile) {
        setTypingUsers(prev => ({
          ...prev,
          [userId]: { username: typingUsername, timestamp: Date.now() }
        }));
      }
    });

    socketRef.current = socket;
    return socket;
  }, [username, roomId, activeFile, files]);

  // Clear old typing indicators
  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      setTypingUsers(prev => {
        const updated = { ...prev };
        Object.keys(updated).forEach(userId => {
          if (now - updated[userId].timestamp > 3000) {
            delete updated[userId];
          }
        });
        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Main initialization effect
  useEffect(() => {
    if (!roomId) {
      navigate('/room');
      return;
    }

    if (initializedRef.current) return;
    initializedRef.current = true;

    const user = (location.state && location.state.username) ||
                 localStorage.getItem('username') ||
                 'Guest';
    setUsername(user);

    const initialize = async () => {
      try {
        const filesData = await fetchFiles();
        setFiles(filesData);
        initializeSocket(filesData);
        localStorage.setItem('username', user);
      } catch (error) {
        console.error('Initialization error:', error);
        setConnectionStatus('Initialization failed');
      }
    };

    initialize();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [roomId, navigate, location.state, fetchFiles, initializeSocket]);

  // Track cursor position
  useEffect(() => {
    if (!editorRef.current || !socketRef.current?.connected) return;

    const editor = editorRef.current;
    
    const handleCursorChange = () => {
      if (isUpdatingFromSocket.current) return;
      
      const selection = editor.getSelection();
      if (selection) {
        const position = {
          lineNumber: selection.positionLineNumber,
          column: selection.positionColumn
        };
        
        socketRef.current.emit('cursor-position', {
          roomId,
          filePath: activeFile,
          position,
          username
        });
      }
    };

    const disposable = editor.onDidChangeCursorPosition(handleCursorChange);
    return () => disposable.dispose();
  }, [roomId, activeFile, username]);

  // Handle code changes with real-time sync
  const handleCodeChange = useCallback((value) => {
    if (!value) value = '';
    if (isUpdatingFromSocket.current) return;
    
    const timestamp = Date.now();
    lastChangeTimestamp.current = timestamp;
    
    setFiles(prev => {
      if (prev[activeFile] !== value) {
        const newFiles = { ...prev, [activeFile]: value };
        
        if (socketRef.current?.connected) {
          const selection = editorRef.current?.getSelection();
          const position = selection ? {
            lineNumber: selection.positionLineNumber,
            column: selection.positionColumn
          } : null;
          
          socketRef.current.emit('code-change', {
            roomId,
            filePath: activeFile,
            code: value,
            position,
            timestamp,
            username
          });

          socketRef.current.emit('user-typing', {
            roomId,
            filePath: activeFile,
            username
          });
        }
        
        debouncedSave(roomId, activeFile, value);
        return newFiles;
      }
      return prev;
    });
  }, [activeFile, roomId, debouncedSave, username]);

  // Handle running the code
  const handleRun = () => {
    if (!files || !iframeRef.current) return;

    const htmlFiles = Object.entries(files).filter(([f]) => f.endsWith('.html'));
    const cssFiles = Object.entries(files).filter(([f]) => f.endsWith('.css'));
    const jsFiles = Object.entries(files).filter(([f]) => f.endsWith('.js'));

    const combined = `
      <!DOCTYPE html>
      <html>
        <head>
          ${cssFiles.map(([_, css]) => `<style>${css}</style>`).join('\n')}
        </head>
        <body>
          ${htmlFiles.map(([_, html]) => html).join('\n')}
          ${jsFiles.map(([_, js]) => `<script>${js}</script>`).join('\n')}
        </body>
      </html>
    `;

    const blob = new Blob([combined], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    iframeRef.current.src = url;
    setTimeout(() => URL.revokeObjectURL(url), 1000);
  };

  const handleDownloadProject = () => {
    const zip = new JSZip();
    Object.entries(files).forEach(([filename, content]) => {
      zip.file(filename, content);
    });

    zip.generateAsync({ type: 'blob' }).then(content => {
      saveAs(content, `${roomId || 'project'}.zip`);
    });
  };

  const handleAddFile = () => {
    const newFileName = prompt('Enter new file name (e.g. `utils/helper.js`):');
    if (newFileName && !files[newFileName]) {
      setFiles(prev => ({
        ...prev,
        [newFileName]: ''
      }));
      setActiveFile(newFileName);
      
      debouncedSave(roomId, newFileName, '');
      
      if (socketRef.current) {
        socketRef.current.emit('new-file', { 
          roomId, 
          filename: newFileName 
        });
      }
    }
  };

  const handleRenameFile = async (oldPath, newPath) => {
    try {
      if (files[newPath]) {
        alert('A file with that name already exists');
        return;
      }
      
      setFiles(prev => {
        const updated = { ...prev };
        updated[newPath] = updated[oldPath];
        delete updated[oldPath];
        return updated;
      });
      
      if (activeFile === oldPath) {
        setActiveFile(newPath);
      }
      
      await debouncedSave(roomId, newPath, files[oldPath]);
      await fetch(`http://localhost:5000/api/code/delete/${roomId}/${encodeURIComponent(oldPath)}`, {
        method: 'DELETE'
      });
    } catch (err) {
      console.error('Error renaming file:', err);
      alert('Error renaming file');
    }
  };

  const handleDeleteFile = async (fileName) => {
    if (fileName === activeFile) {
      const remaining = Object.keys(files).filter(f => f !== fileName);
      setActiveFile(remaining[0] || '');
    }

    try {
      await fetch(`http://localhost:5000/api/code/delete/${roomId}/${encodeURIComponent(fileName)}`, {
        method: 'DELETE'
      });
      
      const updatedFiles = { ...files };
      delete updatedFiles[fileName];
      setFiles(updatedFiles);
      
      if (socketRef.current) {
        socketRef.current.emit('file-deleted', {
          roomId,
          filename: fileName
        });
      }
    } catch (err) {
      console.error('Error deleting file:', err);
      alert('Error deleting file');
    }
  };

  const handleFileSwitch = (fileName) => {
    setActiveFile(fileName);
    if (socketRef.current?.connected) {
      socketRef.current.emit('file-switch', {
        roomId,
        filePath: fileName,
        username
      });
    }
  };

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  useBeforeUnload(() => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }
  });

  if (!files) {
    return (
      <div className="editor-wrapper">
        <div className="loading-screen">
          <div className="loading-spinner"></div>
          <div>Loading collaborative editor...</div>
          <div className="connection-status">{connectionStatus}</div>
        </div>
      </div>
    );
  }

  const typingUsersList = Object.values(typingUsers);

  return (
    <div className="editor-wrapper">
      <div className="collaboration-bar">
        <div className="room-info">
          <strong>Room:</strong> {roomId} | <strong>User:</strong> {username}
          <span className={`connection-status ${connectionStatus === 'Connected' ? 'connected' : 'disconnected'}`}>
            ● {connectionStatus}
          </span>
        </div>
        
        <div className="online-users">
          <span className="users-label">Online ({users.length}):</span>
          {users.map(user => (
            <div 
              key={user.id} 
              className="user-badge"
              style={{ backgroundColor: getUserColor(user.id) }}
            >
              {user.username}
              {user.id === currentUserRef.current && ' (You)'}
            </div>
          ))}
        </div>

        {typingUsersList.length > 0 && (
          <div className="typing-indicator">
            <span className="typing-dots">●●●</span>
            {typingUsersList.map(t => t.username).join(', ')} 
            {typingUsersList.length === 1 ? ' is' : ' are'} typing in {activeFile}...
          </div>
        )}
      </div>

      <div className="editor-container">
        <div className="sidebar">
          <button 
            onClick={handleAddFile} 
            style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
          >
            ➕ Add File
          </button>
          <FileSidebar
            files={Object.keys(files)}
            activeFile={activeFile}
            setActiveFile={handleFileSwitch}
            onAddFile={handleAddFile}
            onDownload={handleDownloadProject}
            onDeleteFile={handleDeleteFile}
            onRenameFile={handleRenameFile}
          />
        </div>

        <div className="main-editor">
          <div className="editor-header">
            <div className="file-info">
              <strong>{activeFile}</strong>
              <span className="file-language">({getLanguage(activeFile)})</span>
            </div>
            <button className="run-button" onClick={handleRun}>
              ▶ Run Code
            </button>
          </div>

          <div className="editor-container-with-cursors">
            <Editor
              height="50vh"
              language={getLanguage(activeFile)}
              value={files[activeFile] || ""}
              onChange={handleCodeChange}
              onMount={editor => {
                editorRef.current = editor;
                editor.onDidChangeCursorPosition(() => {
                  if (socketRef.current?.connected && !isUpdatingFromSocket.current) {
                    const selection = editor.getSelection();
                    if (selection) {
                      const position = {
                        lineNumber: selection.positionLineNumber,
                        column: selection.positionColumn
                      };
                      
                      socketRef.current.emit('cursor-position', {
                        roomId,
                        filePath: activeFile,
                        position,
                        username
                      });
                    }
                  }
                });
              }}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
                wordWrap: 'on',
                automaticLayout: true,
                scrollBeyondLastLine: false,
                renderWhitespace: 'selection',
                smoothScrolling: true,
                cursorBlinking: 'smooth',
                cursorSmoothCaretAnimation: true,
              }}
            />
            
            {Object.entries(userCursors).map(([userId, cursor]) => (
              cursor.activeFile === activeFile && (
                <div
                  key={userId}
                  className="user-cursor"
                  style={{
                    position: 'absolute',
                    top: `${(cursor.position?.lineNumber || 0) * 18}px`,
                    left: `${(cursor.position?.column || 0) * 8}px`,
                    borderLeft: `2px solid ${getUserColor(userId)}`,
                    height: '18px',
                    pointerEvents: 'none',
                    zIndex: 1000,
                  }}
                >
                  <div
                    className="user-cursor-label"
                    style={{
                      backgroundColor: getUserColor(userId),
                      color: 'white',
                      padding: '2px 6px',
                      borderRadius: '3px',
                      fontSize: '11px',
                      position: 'absolute',
                      top: '-20px',
                      left: '0',
                      whiteSpace: 'nowrap',
                    }}
                  >
                    {cursor.username}
                  </div>
                </div>
              )
            ))}
          </div>

          <div className="output-panel">
            <div className="output-header">
              <strong>Output Preview</strong>
              <button className="refresh-output" onClick={handleRun}>
                🔄 Refresh
              </button>
            </div>
            <iframe
              ref={iframeRef}
              title="Live Code Output"
              className="output-frame"
              sandbox="allow-scripts allow-same-origin allow-forms"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditorPage;
