import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { nanoid } from 'nanoid';
import '../styles/style.css'; // Assuming your global styles are here

const RoomPage = () => {
  const navigate = useNavigate();
  const [roomId, setRoomId] = useState('');
  const [showModal, setShowModal] = useState(false);

  const handleCreateRoom = () => {
    const id = nanoid(6);
    setRoomId(id);
    setShowModal(true);
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(roomId);
    alert('Room ID copied to clipboard!');
  };

  const handleJoinRoom = () => {
    if (!roomId.trim()) {
      alert('Please enter a room ID.');
      return;
    }
    navigate(`/editor/${roomId}`);
  };

  return (
    <div className="home-wrapper">
      <div className="card">
        <h2>Real-Time Code Editor</h2>
        <button className="home-button" onClick={handleCreateRoom}>Create Room</button>
        <button className="home-button" onClick={() => setShowModal(true)}>Join Room</button>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>{roomId ? 'Room Created!' : 'Enter Room ID to Join'}</h3>
            <input
              className="home-input"
              type="text"
              placeholder="Enter Room ID"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            {roomId && (
              <button className="home-button" onClick={handleCopy}>
                Copy Room ID
              </button>
            )}
            <button className="home-button" onClick={handleJoinRoom}>Join</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomPage;
