import React from 'react';

const FileSidebar = ({ files, activeFile, setActiveFile, activeUsers = {} }) => {
  const getFileIcon = (fileName) => {
    if (fileName.endsWith('.html')) return 'üìÑ';
    if (fileName.endsWith('.css')) return 'üé®';
    if (fileName.endsWith('.js')) return '‚ö°';
    return 'üìÅ';
  };

  const getFileLanguage = (fileName) => {
    if (fileName.endsWith('.html')) return 'HTML';
    if (fileName.endsWith('.css')) return 'CSS';
    if (fileName.endsWith('.js')) return 'JavaScript';
    return 'Text';
  };

  const getUsersInFile = (fileName) => {
    return Object.values(activeUsers).filter(user => user.activeFile === fileName);
  };

  const userColors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#98D8C8'];
  const getUserColor = (userId) => {
    const index = Object.keys(activeUsers).indexOf(userId);
    return userColors[index % userColors.length];
  };

  return (
    <div className="file-sidebar">
      <h3>üìÅ Project Files</h3>
      <ul className="file-list">
        {files.map((file) => {
          const usersInFile = getUsersInFile(file);
          const isActive = file === activeFile;
          
          return (
            <li
              key={file}
              className={`file-item ${isActive ? 'active' : ''}`}
              onClick={() => setActiveFile(file)}
              title={`${getFileLanguage(file)} file`}
            >
              <span className="file-icon">{getFileIcon(file)}</span>
              <span className="file-name">{file}</span>
              
              {/* Show active users in this file */}
              {usersInFile.length > 0 && (
                <div className="file-users">
                  {usersInFile.map((user, index) => (
                    <div
                      key={index}
                      className="file-user-indicator"
                      style={{ backgroundColor: getUserColor(user.userId) }}
                      title={`${user.username} is editing this file`}
                    >
                      {user.username.charAt(0).toUpperCase()}
                    </div>
                  ))}
                </div>
              )}
              
              {/* Activity indicator for current file */}
              {isActive && (
                <div className="active-file-indicator" title="Currently viewing">
                  üëÅÔ∏è
                </div>
              )}
            </li>
          );
        })}
      </ul>
      
      <div className="sidebar-footer">
        <div className="file-stats">
          <small>{files.length} files</small>
          {Object.keys(activeUsers).length > 0 && (
            <small>{Object.keys(activeUsers).length} active users</small>
          )}
        </div>
      </div>
    </div>
  );
};

export default FileSidebar;
