        <button className="download-button" onClick={onDownload}>â¬‡ Download</button>
      </div>

      <div className="file-list">
        {files.map((file) => (
          <div
            key={file}
            className={`file-item ${file === activeFile ? 'active' : ''}`}
            onClick={() => setActiveFile(file)}
          >
            ðŸ“„ {file}
          </div>
        ))}
      </div>
    </div>
  );
};

