import React, { useState, useEffect } from 'react';
import { FaFolder, FaFolderOpen, FaFileCode, FaPlus, FaEllipsisV } from 'react-icons/fa';

const FileSidebar = ({ tree, activeFile, setActiveFile, onAddFile, onAddFolder, onAddFileToFolder, roomId, setFiles, setFileTree, socketRef, username }) => {
  const [expandedFolders, setExpandedFolders] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  const toggleFolder = (folderPath) => {
    setExpandedFolders(prev => ({ ...prev, [folderPath]: !prev[folderPath] }));
  };

  const handleContextMenu = (e, filePath, type) => {
    e.preventDefault();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      filePath,
      type
    });
  };

  const handleNewFileClick = (e) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      type: 'new-file'
    });
  };

  const handleNewFolderClick = (e) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      type: 'new-folder'
    });
  };

  const handleRename = async (oldPath) => {
    const isFolder = oldPath.endsWith('/');
    const defaultName = isFolder
      ? oldPath.split('/').slice(-2, -1)[0]
      : oldPath.split('/').pop();

    const newName = prompt('Enter new name', defaultName);
    if (!newName || newName === defaultName) return;

    try {
      let newPath;
      if (isFolder) {
        const parts = oldPath.split('/');
        parts.splice(-2, 1, newName);
        newPath = parts.join('/');
      } else {
        const parts = oldPath.split('/');
        parts[parts.length - 1] = newName;
        newPath = parts.join('/');
      }

      const res = await fetch('http://localhost:5000/api/code/rename', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ roomId, oldPath, newPath })
      });

      if (res.ok) {
        setFiles(prev => {
          const newFiles = {};
          for (const key in prev) {
            if (key.startsWith(oldPath)) {
              const updatedKey = key.replace(oldPath, newPath);
              newFiles[updatedKey] = prev[key];
            } else {
              newFiles[key] = prev[key];
            }
          }
          return newFiles;
        });

        setFileTree(prev => {
          if (!prev) return prev;
          const clone = JSON.parse(JSON.stringify(prev));
          const updatePaths = (nodes) => {
            nodes.forEach(node => {
              if (node.path.startsWith(oldPath)) {
                node.path = node.path.replace(oldPath, newPath);
                if (node.type === 'folder') {
                  node.name = newPath.replace(/\/$/, '').split('/').pop();
                }
              }
              if (node.children) updatePaths(node.children);
            });
          };
          updatePaths(clone.children);
          return clone;
        });

        if (socketRef.current?.connected) {
          const eventName = isFolder ? 'folder-renamed' : 'file-renamed';
          socketRef.current.emit(eventName, {
            roomId,
            oldPath,
            newPath,
            username
          });
        }

        if (!isFolder && activeFile === oldPath) {
          setActiveFile(newPath);
        }
      } else {
        throw new Error('Rename failed');
      }
    } catch (error) {
      console.error('Rename error:', error);
      alert('Rename failed. Check console for details.');
    }
  };

  const handleDelete = async (path) => {
    if (!window.confirm(`Are you sure you want to delete ${path}?`)) return;
    const isFolder = path.endsWith('/');

    try {
      const res = await fetch(`http://localhost:5000/api/code/delete/${roomId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ path })
      });

      if (res.ok) {
        setFiles(prev => {
          const updated = {};
          for (const key in prev) {
            if (!key.startsWith(path)) {
              updated[key] = prev[key];
            }
          }
          return updated;
        });

        setFileTree(prev => {
          if (!prev) return prev;
          const clone = JSON.parse(JSON.stringify(prev));
          const removeFolder = (nodes) => {
            for (let i = 0; i < nodes.length; i++) {
              if (nodes[i].path === path) {
                nodes.splice(i, 1);
                return true;
              }
              if (nodes[i].children && removeFolder(nodes[i].children)) {
                return true;
              }
            }
            return false;
          };
          removeFolder(clone.children);
          return clone;
        });

        if (socketRef.current?.connected) {
          const eventName = isFolder ? 'folder-deleted' : 'file-deleted';
          socketRef.current.emit(eventName, {
            roomId,
            path,
            username
          });
        }
      } else {
        throw new Error('Delete failed');
      }
    } catch (error) {
      console.error('Delete error:', error);
      alert('Delete failed. Check console for details.');
    }
  };

  useEffect(() => {
    const handleClickOutside = () => {
      setContextMenu(null);
    };
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const renderTree = (node) => {
    if (node.type === 'file') {
      return (
        <div
          key={node.path}
          className={`file-item ${node.path === activeFile ? 'active' : ''}`}
          onClick={() => setActiveFile(node.path)}
        >
          <FaFileCode />
          <span>{node.name}</span>
          <FaEllipsisV onClick={(e) => {
            e.stopPropagation();
            handleContextMenu(e, node.path, 'file');
          }} />
        </div>
      );
    }

    if (node.type === 'folder') {
      return (
        <div key={node.path} className="folder-container">
          <div className="folder-item" onClick={() => toggleFolder(node.path)}>
            {expandedFolders[node.path] ? <FaFolderOpen /> : <FaFolder />}
            <span>{node.name}</span>
            <FaEllipsisV onClick={(e) => {
              e.stopPropagation();
              handleContextMenu(e, node.path, 'folder');
            }} />
          </div>

          {expandedFolders[node.path] && (
            <div className="folder-contents">
              {node.children.map(child => renderTree(child))}
            </div>
          )}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="file-sidebar">
      <div className="file-sidebar-header">
        <h3>Files</h3>
        <div className="file-actions">
          <button onClick={handleNewFileClick} title="Add File">
            <FaPlus /> File
          </button>
          <button onClick={handleNewFolderClick} title="Add Folder">
            <FaPlus /> Folder
          </button>
        </div>
      </div>

      <div className="file-tree">
        {tree?.children?.map(child => renderTree(child))}
      </div>

      {contextMenu && contextMenu.type === 'new-file' && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('index.html'); }}>New HTML File</div>
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('script.js'); }}>New JS File</div>
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('style.css'); }}>New CSS File</div>
        </div>
      )}

      {contextMenu && contextMenu.type === 'new-folder' && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => {
            const name = prompt('Enter folder name');
            if (name) onAddFolder(name + '/');
            closeContextMenu();
          }}>Create Folder</div>
        </div>
      )}

      {contextMenu && (contextMenu.type === 'file' || contextMenu.type === 'folder') && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => handleRename(contextMenu.filePath)}>Rename</div>
          <div className="context-menu-item" onClick={() => handleDelete(contextMenu.filePath)}>Delete</div>
        </div>
      )}
    </div>
  );
};

export default FileSidebar;
