// client/src/pages/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import { nanoid } from 'nanoid';

const Home = () => {
  const navigate = useNavigate();
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [generatedRoomId, setGeneratedRoomId] = useState('');

  const handleCreateRoom = () => {
    const id = nanoid(6);
    setGeneratedRoomId(id);
    setShowCreateModal(true);
  };

  const handleJoinRoom = () => {
    if (roomId && username) {
      navigate(`/editor/${roomId}`, { state: { username } });
    } else {
      alert('Please enter both username and room ID');
    }
  };

  return (
    <div className="home-wrapper">
      <h1>Home</h1>
      <div className="card">
        <h2>Real-Time Code Collaboration</h2>
        <button className="home-button" onClick={() => setShowCreateModal(true)}>Create Room</button>
        <button className="home-button" onClick={() => setShowJoinModal(true)}>Join Room</button>
      </div>

      {showCreateModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Room Created</h3>
            <p>Room ID: <strong>{generatedRoomId}</strong></p>
            <button onClick={() => setShowCreateModal(false)}>Close</button>
          </div>
        </div>
      )}

      {showJoinModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Join Room</h3>
            <input
              type="text"
              placeholder="Enter your name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="home-input"
            />
            <input
              type="text"
              placeholder="Room ID"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
              className="home-input"
            />
            <button className="home-button" onClick={handleJoinRoom}>Enter Room</button>
            <button onClick={() => setShowJoinModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
