import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useLocation, useParams, useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';
import FileSidebar from '../components/FileSidebar';
import '../styles/editor.css';

function EditorPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const initializedRef = useRef(false); // Add this ref to track initialization

  const [username, setUsername] = useState('');
  const [files, setFiles] = useState(null); // Start with null to distinguish initial state
  const [activeFile, setActiveFile] = useState('index.html');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const editorRef = useRef(null);
  const iframeRef = useRef(null);

  // Debounced save function
  const debouncedSave = useCallback(
    debounce(async (roomId, filename, content) => {
      try {
        await fetch('http://localhost:5000/api/code/save', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ roomId, filename, content }),
        });
      } catch (err) {
        console.error('Error saving file:', err);
      }
    }, 1000),
    []
  );

  // Fetch files from backend - stable reference
  const fetchFiles = useCallback(async () => {
    if (!roomId) return;

    try {
      const response = await fetch(`http://localhost:5000/api/code/${roomId}`);
      
      if (!response.ok) {
        if (response.status === 404) {
          // Initialize with default files if room doesn't exist
          const defaultFiles = {
            'index.html': '<!-- HTML file -->\n',
            'style.css': '/* CSS file */\n',
            'script.js': '// JS file\n',
          };
          
          // Save defaults to DB
          await Promise.all(
            Object.entries(defaultFiles).map(([filename, content]) =>
              debouncedSave(roomId, filename, content)
            )
          );
          
          return defaultFiles;
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      return Object.keys(data).length > 0 ? data : {
        'index.html': '<!-- HTML file -->\n',
        'style.css': '/* CSS file */\n',
        'script.js': '// JS file\n',
      };
    } catch (error) {
      console.error('Error loading files:', error);
      setConnectionStatus('Failed to load files. Trying again...');
      // Return default files on error
      return {
        'index.html': '<!-- HTML file -->\n',
        'style.css': '/* CSS file */\n',
        'script.js': '// JS file\n',
      };
    }
  }, [roomId, debouncedSave]);

  // Initialize socket connection - stable reference
  const initializeSocket = useCallback((filesData) => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }

    const socket = io('http://localhost:5000', {
      transports: ['websocket'],
      upgrade: false,
      reconnectionAttempts: 3, // Reduced from 5 to 3
      reconnectionDelay: 3000, // Increased from 1000 to 3000
    });

    socket.on('connect', () => {
      console.log('Socket connected');
      setConnectionStatus('Connected');
      
      if (username && roomId) {
        socket.emit('join-room', { 
          roomId, 
          username,
          files: filesData
        });
      }
    });

    socket.on('disconnect', () => {
      console.log('Socket disconnected');
      setConnectionStatus('Disconnected');
    });

    socket.on('receive-code', ({ filePath, code }) => {
      setFiles(prev => ({ ...prev, [filePath]: code }));
    });

    socket.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
      setConnectionStatus('Connection error');
    });

    socketRef.current = socket;
    return socket;
  }, [username, roomId]);

  // Main initialization effect - runs once
  useEffect(() => {
    if (!roomId) {
      navigate('/room');
      return;
    }

    // Prevent multiple initializations
    if (initializedRef.current) return;
    initializedRef.current = true;

    const user = (location.state && location.state.username) ||
                 localStorage.getItem('username') ||
                 'Guest';
    setUsername(user);

    const initialize = async () => {
      try {
        const filesData = await fetchFiles();
        setFiles(filesData);
        initializeSocket(filesData);
        localStorage.setItem('username', user);
      } catch (error) {
        console.error('Initialization error:', error);
        setConnectionStatus('Initialization failed');
      }
    };

    initialize();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [roomId, navigate, location.state, fetchFiles, initializeSocket]);

  const handleCodeChange = (value) => {
    if (!value) value = '';
    
    setFiles(prev => ({ ...prev, [activeFile]: value }));

    if (socketRef.current?.connected) {
      socketRef.current.emit('code-change', {
        roomId,
        filePath: activeFile,
        code: value,
      });
    }

    debouncedSave(roomId, activeFile, value);
  };

  const handleRun = () => {
    if (!files || !iframeRef.current) return;

    const html = files['index.html'] || '';
    const css = files['style.css'] || '';
    const js = files['script.js'] || '';

    const combined = `
      <!DOCTYPE html>
      <html>
        <head>
          <style>${css}</style>
        </head>
        <body>
          ${html}
          <script>${js}</script>
        </body>
      </html>
    `;

    const blob = new Blob([combined], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    iframeRef.current.src = url;
    setTimeout(() => URL.revokeObjectURL(url), 1000);
  };

  const getLanguage = (file) => {
    if (file.endsWith('.js')) return 'javascript';
    if (file.endsWith('.html')) return 'html';
    if (file.endsWith('.css')) return 'css';
    return 'plaintext';
  };

  if (!files) {
    return (
      <div className="editor-wrapper">
        <div className="loading-screen">
          <div>Loading editor...</div>
          <div>{connectionStatus}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="editor-wrapper">
      <div className="sidebar">
        <FileSidebar
          files={Object.keys(files)}
          activeFile={activeFile}
          setActiveFile={setActiveFile}
        />
      </div>

      <div className="main-editor">
        <div className="editor-header">
          <div>
            <strong>Room:</strong> {roomId}
            <br />
            <span>Welcome, {username}!</span>
            <br />
            <span className={`connection-status ${connectionStatus === 'Connected' ? 'connected' : 'disconnected'}`}>
              ● {connectionStatus}
            </span>
          </div>
          <button className="run-button" onClick={handleRun}>
            Run ▶
          </button>
        </div>

        <Editor
          height="50vh"
          language={getLanguage(activeFile)}
          value={files[activeFile] || ""}
          onChange={handleCodeChange}
          onMount={editor => editorRef.current = editor}
          theme="vs-dark"
          options={{
            minimap: { enabled: false },
            fontSize: 14,
            wordWrap: 'on',
            automaticLayout: true,
          }}
        />

        <div className="output-panel">
          <iframe
            ref={iframeRef}
            title="Output Preview"
            className="output-frame"
            sandbox="allow-scripts allow-same-origin"
          />
        </div>
      </div>
    </div>
  );
}

function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

export default EditorPage;
