import React, { useState, useEffect } from 'react';
import { FaFolder, FaFolderOpen, FaFileCode, FaPlus, FaEllipsisV } from 'react-icons/fa';

const FileSidebar = ({ tree, activeFile, setActiveFile, onAddFile, onAddFolder, onAddFileToFolder, roomId, setFiles, setFileTree, socketRef, username }) => {
  const [expandedFolders, setExpandedFolders] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  const toggleFolder = (folderPath) => {
    setExpandedFolders(prev => ({ ...prev, [folderPath]: !prev[folderPath] }));
  };

  const handleContextMenu = (e, filePath, type) => {
    e.preventDefault();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      filePath,
      type
    });
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  const handleNewFileClick = (e) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      type: 'new-file'
    });
  };

  const handleNewFolderClick = (e) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    setContextMenu({
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX,
      type: 'new-folder'
    });
  };

  useEffect(() => {
    const handleClickOutside = () => {
      setContextMenu(null);
    };
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const renderTree = (node) => {
    if (node.type === 'file') {
      return (
        <div
          key={node.path}
          className={`file-item ${node.path === activeFile ? 'active' : ''}`}
          onClick={() => setActiveFile(node.path)}
        >
          <FaFileCode />
          <span>{node.name}</span>
          <FaEllipsisV onClick={(e) => {
            e.stopPropagation();
            handleContextMenu(e, node.path, 'file');
          }} />
        </div>
      );
    }

    if (node.type === 'folder') {
      return (
        <div key={node.path} className="folder-container">
          <div className="folder-item" onClick={() => toggleFolder(node.path)}>
            {expandedFolders[node.path] ? <FaFolderOpen /> : <FaFolder />}
            <span>{node.name}</span>
            <FaEllipsisV onClick={(e) => {
              e.stopPropagation();
              handleContextMenu(e, node.path, 'folder');
            }} />
          </div>

          {expandedFolders[node.path] && (
            <div className="folder-contents">
              {node.children.map(child => renderTree(child))}
            </div>
          )}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="file-sidebar">
      <div className="file-sidebar-header">
        <h3>Files</h3>
        <div className="file-actions">
          <button onClick={handleNewFileClick} title="Add File">
            <FaPlus /> File
          </button>
          <button onClick={handleNewFolderClick} title="Add Folder">
            <FaPlus /> Folder
          </button>
        </div>
      </div>

      <div className="file-tree">
        {tree?.children?.map(child => renderTree(child))}
      </div>

      {contextMenu && contextMenu.type === 'new-file' && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('index.html'); }}>New HTML File</div>
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('script.js'); }}>New JS File</div>
          <div className="context-menu-item" onClick={() => { closeContextMenu(); onAddFile('style.css'); }}>New CSS File</div>
        </div>
      )}

      {contextMenu && contextMenu.type === 'new-folder' && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => {
            const name = prompt('Enter folder name');
            if (name) onAddFolder(name + '/');
            closeContextMenu();
          }}>Create Folder</div>
        </div>
      )}

      {contextMenu && (contextMenu.type === 'file' || contextMenu.type === 'folder') && (
        <div
          className="context-menu"
          style={{ top: contextMenu.top, left: contextMenu.left, position: 'absolute', zIndex: 1000 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="context-menu-item" onClick={() => handleRename(contextMenu.filePath)}>Rename</div>
          <div className="context-menu-item" onClick={() => handleDelete(contextMenu.filePath)}>Delete</div>
        </div>
      )}
    </div>
  );
};

export default FileSidebar;
