// client/src/pages/RoomPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { nanoid } from 'nanoid';
giv
function RoomPage() {
  const navigate = useNavigate();
  const [roomId, setRoomId] = useState('');
  const [showRoomId, setShowRoomId] = useState(false);

  const createRoom = () => {
    const id = nanoid(6);
    setRoomId(id);
    setShowRoomId(true);
  };

  const joinRoom = () => {
    const id = prompt('Enter Room ID');
    if (id) {
      const username = localStorage.getItem('username');
      navigate(`/editor/${id}`, { state: { username } });
    }
  };

  const copyRoomId = () => {
    navigator.clipboard.writeText(roomId);
    alert('Room ID copied to clipboard!');
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
      <h2>Home</h2>
      <div style={{ border: '1px solid #ccc', padding: '2rem', borderRadius: '8px', textAlign: 'center' }}>
        <h3>Real-Time Code Collaboration</h3>
        <button onClick={createRoom} style={{ margin: '0.5rem' }}>Create Room</button>
        <button onClick={joinRoom} style={{ margin: '0.5rem' }}>Join Room</button>
        {showRoomId && (
          <div style={{ marginTop: '1rem' }}>
            <strong>Room ID:</strong> {roomId}
            <button onClick={copyRoomId} style={{ marginLeft: '1rem' }}>Copy</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default RoomPage;
