import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { nanoid } from 'nanoid';
import '../styles/Home.css'; // ensure this path is correct

function RoomPage() {
  const navigate = useNavigate();
  const [roomId, setRoomId] = useState('');
  const [showRoomId, setShowRoomId] = useState(false);

  const createRoom = () => {
    const id = nanoid(6);
    setRoomId(id);
    setShowRoomId(true);
  };

  const joinRoom = () => {
    if (!roomId.trim()) {
      alert('Please enter Room ID!');
      return;
    }
    const username = localStorage.getItem('username') || 'Guest';
    navigate(`/editor/${roomId}`, { state: { username } });
  };

  const copyRoomId = () => {
    navigator.clipboard.writeText(roomId);
    alert('Room ID copied to clipboard!');
  };

  return (
    <div className="home-wrapper">
      <div className="card">
        <h2>Real-Time Code Collaboration</h2>
        <button className="home-button" onClick={createRoom}>Create Room</button>
        {showRoomId && (
          <>
            <p><strong>Room ID:</strong> {roomId}</p>
            <button className="home-button" onClick={copyRoomId}>Copy Room ID</button>
          </>
        )}
        <input
          className="home-input"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button className="home-button" onClick={joinRoom}>Join Room</button>
      </div>
    </div>
  );
}

export default RoomPage;
