import React, { useEffect, useRef, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000'); // Change if deployed

function EditorPage() {
  const { roomId } = useParams();
  const { state } = useLocation(); // contains username
  const [code, setCode] = useState('// Start coding...');
  const editorRef = useRef(null);

  useEffect(() => {
    if (state?.username) {
      socket.emit('join-room', { roomId, username: state.username });
    }

    // Listen for code from others
    socket.on('receive-code', ({ code }) => {
      if (code !== editorRef.current?.getValue()) {
        setCode(code); // Update state if new code is received
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [roomId, state]);

  const handleCodeChange = (value) => {
    setCode(value);
    socket.emit('code-change', { roomId, filePath: 'main.js', code: value });
  };

  const handleEditorDidMount = (editor) => {
    editorRef.current = editor;
  };

  return (
    <div style={{ padding: '10px' }}>
      <h3>Room: {roomId}</h3>
      <p>Welcome, {state?.username || 'Guest'}!</p>
      <Editor
        height="80vh"
        defaultLanguage="javascript"
        value={code}
        onChange={handleCodeChange}
        onMount={handleEditorDidMount}
        theme="vs-dark"
      />
    </div>
  );
}

export default EditorPage;
